$(document).ready(function(){$(".cart-quantity-input").change(function(){var t=$(this).attr("cid");$("#cart-btn-edit-"+t).hide(),$("#cart-btn-save-"+t).show()}),$(".qty-minus").bind("click",function(){var t=parseInt($(this).next().val());t>1&&(t-=1,$(this).next().val(t).trigger("change"))}),$(".qty-plus").bind("click",function(){var t=parseInt($(this).prev().val());t+=1,$(this).prev().val(t).trigger("change")}),$(".cart-a-remove").click(function(){$(this).parents(".cart-line").remove(),$("#form-submit-edit-cart").submit()}),$(".cart-btn-save").click(function(){$("#form-submit-edit-cart").submit()}),$(".cart-btn-edit").click(function(t){t.preventDefault(),$(this).parents(".cart-line").find(".change-select-cart").toggle(400)})});